#include <stdio.h>

void main(){
   
    short int i = 1; //sugestão de endereço: 192
    short int v[4];  //sugestão de endereço: 200
   
    while (i <= 4){
        v[i] = i++;
        printf("%d = %d\n", &v[i], i);
        
    }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            
    printf("\n");
    for(i=0; i<4; i++){
        printf("%d = %d\n", &v[i], v[i]);
    }
    
}